numbers = [1, 2, 3, 4, 5]
squares = {x: x**2 for x in numbers if x % 2 == 0}
print(squares)

words = ['apple', 'banana', 'avocado']
vowels = {w: w[0] for w in words if w[0] in 'aeiou'}
print(vowels)

nums = [10, 20, 30]
double = {n // 2 for n in nums}
print(double)

chars = ['x', 'y', 'z']
indices = {i: c for i, c in enumerate(chars)}
print(indices)

items = ['bag', 'book', 'bottle']
lens = {item: len(item) for item in items if 'b' in item}
print(lens)
